// taskBgColor — фоновый цвет индикатора задачи.
func taskBgColor(status string) string {
	switch status {
	case "ok":
		return "bg-status-success"
	case "error":
		return "bg-status-error"
	case "pending":
		return "bg-status-warning"
	default:
		return "bg-text-muted"
	}
}

